const fullPath = $json.args;
const parts = fullPath.split('/');
const lastPart = parts[parts.length - 1];

let folderName = "";
let fileName = "";

// Check if the last part of the path looks like a file
if (lastPart.includes('.')) {
  // It's a file path like "folder/file.png"
  fileName = parts.pop();
  folderName = parts.join('/');
} else {
  // It's a folder path like "folder/subfolder" or just "folder"
  fileName = "";
  folderName = lastPart; // The folder we want is the LAST part of the path
}

return [{
  json: {
    folderName: folderName,
    fileName: fileName
  }
}];

// Generate a simple 6-digit random number for the ID
const confirmationId = Math.floor(100000 + Math.random() * 900000);

// Get the File/Folder ID and Name from the direct input (from the Merge node)
const fileId = $input.item.json.id;
const fileName = $input.item.json.name;

// Get the original sender's number from the first Code node
const fromNumber = $node["Parse Command"].json.from;

// Remove the "whatsapp:" prefix for the 'To' field
const correctedFromNumber = fromNumber.replace('whatsapp:', '');

// Return a new data item with all necessary info
return [{
  json: {
    confirmationId: confirmationId,
    fileId: fileId,
    fileName: fileName,
    from: correctedFromNumber
  }
}];

const raw = (items[0].json.body.Body || '').trim();
const from = (items[0].json.body.From || '');

const parts = raw.split(/\s+/);
let command = (parts[0] || '').toUpperCase();
let args = "";
let destination = "";

// Special handling for MOVE command structure
if (command === "MOVE") {
  destination = parts.pop();
  args = parts.slice(1).join(' ').trim();

  // Remove leading slash from destination
  if (destination && destination.startsWith('/')) {
    destination = destination.substring(1);
  }
} else {
  // Logic for all other commands
  args = parts.slice(1).join(' ').trim();
}

// Remove leading slash from the main argument if it's a path
if (args.startsWith('/')) {
  args = args.substring(1);
}

// NEW: Set command to INVALID if args are required but missing
if ((command === "LIST" || command === "DELETE" || command === "SUMMARY") && (!args || args === "")) {
  command = "INVALID";
  args = "Missing required argument";
}

if (command === "MOVE" && (!args || args === "" || !destination || destination === "")) {
  command = "INVALID";
  args = "Missing required arguments (source and destination)";
}

if (command === "CONFIRM" && (!args || args === "")) {
  command = "INVALID";
  args = "Missing confirmation ID";
}

return [{
  json: {
    raw,
    from,
    command,
    args,
    destination
  }
}];

