// Enhanced code for "Prepare Gemini Request" node with debugging and fallbacks
// This should replace the current JavaScript code in node ID: add5d1e1-b357-437c-9932-51e0f6acab49

// Process all input items
const results = [];

for (let i = 0; i < $input.all().length; i++) {
  const item = $input.all()[i];
  const mimeType = item.json.mimeType;
  const fileName = item.json.name;

  console.log(`Processing item ${i}: ${fileName} (${mimeType})`);

  let base64Data = null;
  let binaryData = null;

  try {
    // Method 1: Try getBinaryDataBuffer (recommended approach)
    console.log('Attempting getBinaryDataBuffer...');
    binaryData = await this.helpers.getBinaryDataBuffer(i, 'data');

    if (binaryData && binaryData.length > 0) {
      base64Data = binaryData.toString('base64');
      console.log(`Successfully got binary data via getBinaryDataBuffer: ${binaryData.length} bytes`);
    } else {
      console.log('getBinaryDataBuffer returned empty or null data');
    }

  } catch (error) {
    console.log('getBinaryDataBuffer failed:', error.message);
  }

  // Method 2: Fallback to direct binary access if first method fails
  if (!base64Data && item.binary && item.binary.data) {
    try {
      console.log('Attempting direct binary access...');

      // Try accessing the raw data
      if (item.binary.data.data) {
        // Convert Buffer to base64 if it's a Buffer object
        if (Buffer.isBuffer(item.binary.data.data)) {
          base64Data = item.binary.data.data.toString('base64');
          console.log(`Successfully got binary data via direct access (Buffer): ${item.binary.data.data.length} bytes`);
        } else if (typeof item.binary.data.data === 'string') {
          // If it's already a base64 string, use it directly
          base64Data = item.binary.data.data;
          console.log('Found existing base64 string in binary data');
        }
      }
    } catch (error) {
      console.log('Direct binary access failed:', error.message);
    }
  }

  // Method 3: Last resort - try alternative binary property access
  if (!base64Data && item.binary && item.binary.data && item.binary.data.buffer) {
    try {
      console.log('Attempting buffer property access...');
      const buffer = Buffer.from(item.binary.data.buffer);
      base64Data = buffer.toString('base64');
      console.log(`Successfully got binary data via buffer property: ${buffer.length} bytes`);
    } catch (error) {
      console.log('Buffer property access failed:', error.message);
    }
  }

  if (!base64Data) {
    console.error(`Failed to get binary data for ${fileName}. Item binary structure:`, JSON.stringify(item.binary, null, 2));
    // Skip this item or throw an error
    continue;
  }

  // Verify base64 data is not empty
  if (base64Data.length === 0) {
    console.error(`Binary data is empty for ${fileName}`);
    continue;
  }

  console.log(`Final base64 data length for ${fileName}: ${base64Data.length} characters`);

  // Determine the appropriate MIME type for Gemini API
  let apiMimeType = mimeType;

  // Handle Google Apps files - convert to text/plain for Gemini
  if (mimeType.startsWith('application/vnd.google-apps')) {
    apiMimeType = 'text/plain';
  }

  // Create appropriate prompt based on file type
  const prompt = mimeType.startsWith('image/') 
    ? 'Describe this image in detail in a few bullet points.'
    : 'Provide a concise, bullet-point summary of the following document. Focus on the main topics and key conclusions.';

  // Construct the request body for Gemini API
  const requestBody = {
    contents: [{
      parts: [
        { text: prompt },
        {
          inlineData: {
            mimeType: apiMimeType,
            data: base64Data
          }
        }
      ]
    }]
  };

  // Add to results array
  results.push({
    json: {
      requestBody: requestBody,
      fileName: fileName,
      originalMimeType: mimeType,
      apiMimeType: apiMimeType,
      dataLength: base64Data.length // For debugging
    }
  });
}

if (results.length === 0) {
  throw new Error('No files could be processed - all binary data access methods failed');
}

console.log(`Successfully processed ${results.length} files`);
return results;

